function computeHeight(e){var t=e+" cm / ";var n=Math.floor(e*.0328084);var r=Math.round(e*.3937008-n*12);t=t+n+" ' "+r+"''";document.getElementById("heightValue").innerHTML=t;document.getElementById("inputHeight").value=e}function computeWeight(e){var t=e+" kg / ";var n=Math.round(e*2.204623);t=t+n+" lbs";document.getElementById("weightValue").innerHTML=t;document.getElementById("inputWeight").value=e}function convert(e,t){if("weight"==e){computeWeight(t)}else if("height"==e){computeHeight(t)}}function updateSlider(e,t){convert(e,t)}function createSlider(e){var t=document.getElementById(e+"Min").value;var n=document.getElementById(e+"Max").value;var r=document.getElementById(e+"CurrentValue").value;if(r==""){r=t}$("#"+e+"-slider").slider({range:false,min:parseInt(t),max:parseInt(n),value:parseInt(r)}).on("slide",function(t,n){updateSlider(e,t.value)});updateSlider(e,r)}function bindSliders(){createSlider("weight");createSlider("height")}